// Agricultural Education Knowledge Graph Analysis Engine
// Analyzes student learning patterns and generates actionable insights

const student = $input.first().json;
const knowledgeGraph = $input.all()[1].json;
const competencyData = $input.all()[2].json || [];
const recentActivities = $input.all()[3].json || [];

// Core analysis functions
const analysis = {
  student_id: student.id,
  analysis_timestamp: new Date().toISOString(),
  competency_analysis: analyzeCompetencyProgression(),
  learning_patterns: analyzeLearningPatterns(),
  skill_gaps: identifySkillGaps(),
  peer_comparisons: generatePeerComparisons(),
  recommendations: generateRecommendations()
};

function analyzeCompetencyProgression() {
  const competencies = {};
  const ffaStandards = ['AS.07.01', 'AS.07.02', 'AS.07.03', 'AS.07.04'];

  // Initialize all FFA standards
  ffaStandards.forEach(standard => {
    competencies[standard] = {
      demonstrations: 0,
      average_quality: 0,
      last_demonstration: null,
      progression_trend: 'no_data',
      mastery_level: 'novice',
      consistency_score: 0
    };
  });

  // Process competency data
  competencyData.forEach(comp => {
    if (competencies[comp.competency]) {
      competencies[comp.competency] = {
        demonstrations: comp.demonstrations || 0,
        average_quality: calculateAverageQuality(comp.quality_scores),
        last_demonstration: comp.last_demonstration,
        progression_trend: calculateProgressionTrend(comp.quality_scores),
        mastery_level: determineMasteryLevel(comp),
        consistency_score: calculateConsistency(comp.quality_scores)
      };
    }
  });

  // Analyze knowledge graph relationships
  if (knowledgeGraph.relationships) {
    knowledgeGraph.relationships
      .filter(rel => rel.type === 'PARTICIPATED_IN' && rel.properties.competency)
      .forEach(rel => {
        const competency = rel.properties.competency;
        if (competencies[competency]) {
          competencies[competency].graph_connections = (competencies[competency].graph_connections || 0) + 1;
        }
      });
  }

  return competencies;
}

function analyzeLearningPatterns() {
  const patterns = {
    learning_frequency: calculateLearningFrequency(),
    preferred_activities: identifyPreferredActivities(),
    time_patterns: analyzeTimePatterns(),
    concept_mastery_rate: calculateConceptMasteryRate(),
    supervision_dependency: analyzSupervisionDependency()
  };

  return patterns;
}

function identifySkillGaps() {
  const gaps = [];
  const expectedLevels = {
    'AS.07.01': { min_demonstrations: 5, target_level: 'proficient' },
    'AS.07.02': { min_demonstrations: 4, target_level: 'proficient' },
    'AS.07.03': { min_demonstrations: 3, target_level: 'developing' },
    'AS.07.04': { min_demonstrations: 3, target_level: 'developing' }
  };

  Object.entries(expectedLevels).forEach(([competency, expected]) => {
    const current = analysis.competency_analysis[competency];

    if (current.demonstrations < expected.min_demonstrations) {
      gaps.push({
        competency: competency,
        gap_type: 'demonstration_count',
        current_demonstrations: current.demonstrations,
        needed_demonstrations: expected.min_demonstrations - current.demonstrations,
        priority: calculateGapPriority(competency, 'demonstration_count'),
        recommended_activities: getRecommendedActivities(competency)
      });
    }

    if (current.average_quality < 70 && current.demonstrations > 0) {
      gaps.push({
        competency: competency,
        gap_type: 'quality_improvement',
        current_quality: current.average_quality,
        target_quality: 80,
        priority: calculateGapPriority(competency, 'quality_improvement'),
        improvement_suggestions: getQualityImprovementSuggestions(competency)
      });
    }
  });

  return gaps;
}

function generatePeerComparisons() {
  // Note: In production, this would compare with anonymized peer data
  return {
    competency_percentile: calculateCompetencyPercentile(),
    activity_frequency_percentile: calculateActivityPercentile(),
    quality_percentile: calculateQualityPercentile(),
    areas_above_peers: identifyStrengthAreas(),
    areas_below_peers: identifyImprovementAreas()
  };
}

function generateRecommendations() {
  const recommendations = [];

  // Skill gap recommendations
  analysis.skill_gaps.forEach(gap => {
    recommendations.push({
      type: 'skill_development',
      title: `Improve ${gap.competency} ${gap.gap_type.replace('_', ' ')}`,
      description: generateGapDescription(gap),
      priority: gap.priority,
      action_items: gap.recommended_activities || gap.improvement_suggestions || [],
      estimated_time: estimateTimeToImprove(gap),
      resources: getRecommendedResources(gap.competency)
    });
  });

  // Learning pattern recommendations
  if (analysis.learning_patterns.learning_frequency < 0.5) {
    recommendations.push({
      type: 'engagement_improvement',
      title: 'Increase Learning Activity Frequency',
      description: 'Your learning activity frequency is below optimal levels. Consider setting daily or weekly learning goals.',
      priority: 'medium',
      action_items: [
        'Set up daily learning reminders',
        'Schedule regular animal health checks',
        'Join study groups with peers'
      ]
    });
  }

  // Knowledge graph insights
  const missingConnections = findMissingKnowledgeConnections();
  missingConnections.forEach(connection => {
    recommendations.push({
      type: 'knowledge_connection',
      title: `Explore: ${connection.concept_a} â†’ ${connection.concept_b}`,
      description: `Understanding the relationship between ${connection.concept_a} and ${connection.concept_b} will strengthen your agricultural knowledge.`,
      priority: 'low',
      educational_value: connection.value,
      suggested_activities: connection.activities
    });
  });

  // Sort by priority
  return recommendations.sort((a, b) => {
    const priorityOrder = { 'high': 3, 'medium': 2, 'low': 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });
}

// Helper functions
function calculateAverageQuality(scores) {
  if (!scores || scores.length === 0) return 0;
  const parsedScores = Array.isArray(scores) ? scores : JSON.parse(scores || '[]');
  return parsedScores.reduce((sum, score) => sum + score, 0) / parsedScores.length;
}

function calculateProgressionTrend(scores) {
  if (!scores || scores.length < 2) return 'insufficient_data';
  const parsedScores = Array.isArray(scores) ? scores : JSON.parse(scores || '[]');
  const recent = parsedScores.slice(-3);
  const earlier = parsedScores.slice(-6, -3);

  if (recent.length === 0 || earlier.length === 0) return 'insufficient_data';

  const recentAvg = recent.reduce((sum, score) => sum + score, 0) / recent.length;
  const earlierAvg = earlier.reduce((sum, score) => sum + score, 0) / earlier.length;

  if (recentAvg > earlierAvg + 5) return 'improving';
  if (recentAvg < earlierAvg - 5) return 'declining';
  return 'stable';
}

function determineMasteryLevel(competency) {
  const demos = competency.demonstrations || 0;
  const quality = calculateAverageQuality(competency.quality_scores);

  if (demos >= 8 && quality >= 85) return 'advanced';
  if (demos >= 5 && quality >= 75) return 'proficient';
  if (demos >= 3 && quality >= 60) return 'developing';
  return 'novice';
}

function calculateConsistency(scores) {
  if (!scores || scores.length < 2) return 0;
  const parsedScores = Array.isArray(scores) ? scores : JSON.parse(scores || '[]');
  const mean = parsedScores.reduce((sum, score) => sum + score, 0) / parsedScores.length;
  const variance = parsedScores.reduce((sum, score) => sum + Math.pow(score - mean, 2), 0) / parsedScores.length;
  const stdDev = Math.sqrt(variance);
  return Math.max(0, 100 - stdDev); // Higher consistency = lower standard deviation
}

function calculateLearningFrequency() {
  const days = 30;
  const activitiesInPeriod = recentActivities.length;
  return activitiesInPeriod / days; // Activities per day
}

function identifyPreferredActivities() {
  const activityCounts = {};
  recentActivities.forEach(activity => {
    activityCounts[activity.activity_type] = (activityCounts[activity.activity_type] || 0) + 1;
  });

  return Object.entries(activityCounts)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 3)
    .map(([type, count]) => ({ type, count, percentage: (count / recentActivities.length) * 100 }));
}

function analyzeTimePatterns() {
  const hourCounts = {};
  recentActivities.forEach(activity => {
    const hour = new Date(activity.timestamp).getHours();
    hourCounts[hour] = (hourCounts[hour] || 0) + 1;
  });

  const peakHour = Object.entries(hourCounts)
    .sort(([,a], [,b]) => b - a)[0];

  return {
    peak_learning_hour: peakHour ? parseInt(peakHour[0]) : null,
    learning_distribution: hourCounts,
    consistency: calculateTimeConsistency(hourCounts)
  };
}

function calculateConceptMasteryRate() {
  const conceptCounts = {};
  recentActivities.forEach(activity => {
    try {
      const concepts = JSON.parse(activity.concepts_covered || '[]');
      concepts.forEach(concept => {
        conceptCounts[concept] = (conceptCounts[concept] || 0) + 1;
      });
    } catch (e) {
      // Skip invalid JSON
    }
  });

  return Object.keys(conceptCounts).length / 30; // New concepts per day
}

function analyzSupervisionDependency() {
  const supervisedCount = recentActivities.filter(activity => activity.supervisor).length;
  return supervisedCount / recentActivities.length; // Percentage of supervised activities
}

function calculateGapPriority(competency, gapType) {
  const competencyPriority = {
    'AS.07.01': 'high',    // Health management is critical
    'AS.07.02': 'high',   // Disease prevention is critical
    'AS.07.03': 'medium', // Treatment protocols
    'AS.07.04': 'medium'  // Health procedures
  };

  return competencyPriority[competency] || 'low';
}

function getRecommendedActivities(competency) {
  const activities = {
    'AS.07.01': [
      'Perform daily health checks on assigned animals',
      'Practice body condition scoring',
      'Create health management protocols',
      'Shadow veterinarian during farm visits'
    ],
    'AS.07.02': [
      'Design biosecurity protocols',
      'Create vaccination schedules',
      'Study disease transmission pathways',
      'Implement quarantine procedures'
    ],
    'AS.07.03': [
      'Practice treatment administration under supervision',
      'Develop treatment decision trees',
      'Study common agricultural animal diseases',
      'Practice diagnostic techniques'
    ],
    'AS.07.04': [
      'Practice animal restraint techniques',
      'Learn to use veterinary instruments',
      'Document health procedures thoroughly',
      'Practice injection techniques on models'
    ]
  };

  return activities[competency] || ['Consult with instructor for specific activities'];
}

function getQualityImprovementSuggestions(competency) {
  return [
    'Include more detailed observations in records',
    'Explain reasoning behind decisions',
    'Use proper agricultural terminology',
    'Seek feedback from instructors or veterinarians',
    'Practice procedures multiple times for consistency'
  ];
}

function findMissingKnowledgeConnections() {
  // Simplified version - in production would use more sophisticated graph analysis
  return [
    {
      concept_a: 'animal_nutrition',
      concept_b: 'disease_prevention',
      value: 85,
      activities: ['Study nutritional impacts on immune system']
    },
    {
      concept_a: 'biosecurity',
      concept_b: 'herd_management',
      value: 80,
      activities: ['Design facility layouts with biosecurity in mind']
    }
  ];
}

function generateGapDescription(gap) {
  if (gap.gap_type === 'demonstration_count') {
    return `You need ${gap.needed_demonstrations} more practice sessions with ${gap.competency} to reach proficiency level.`;
  } else if (gap.gap_type === 'quality_improvement') {
    return `Your current quality score for ${gap.competency} is ${Math.round(gap.current_quality)}%. Focus on detailed observations and proper documentation to reach the target of ${gap.target_quality}%.`;
  }
  return 'Skill development needed in this area.';
}

function estimateTimeToImprove(gap) {
  if (gap.gap_type === 'demonstration_count') {
    return `${gap.needed_demonstrations * 2} weeks with regular practice`;
  }
  return '2-4 weeks with focused effort';
}

function getRecommendedResources(competency) {
  return [
    'FFA Agricultural Education Curriculum Guide',
    'NCBI Animal Health Resources',
    'Veterinary Extension Programs',
    'Agricultural Education Online Modules'
  ];
}

function calculateTimeConsistency(hourCounts) {
  const hours = Object.keys(hourCounts).length;
  const maxHours = 24;
  return (hours / maxHours) * 100; // Percentage of hours with activity
}

function calculateCompetencyPercentile() {
  // Placeholder - would use actual peer data in production
  return Math.floor(Math.random() * 40) + 30; // 30-70th percentile
}

function calculateActivityPercentile() {
  // Placeholder - would use actual peer data in production
  return Math.floor(Math.random() * 40) + 30;
}

function calculateQualityPercentile() {
  // Placeholder - would use actual peer data in production
  return Math.floor(Math.random() * 40) + 30;
}

function identifyStrengthAreas() {
  return ['animal_observation', 'record_keeping'];
}

function identifyImprovementAreas() {
  return ['treatment_protocols', 'diagnostic_skills'];
}

return analysis;
