// Configure Zep Memory Settings via API
// This runs once to set up your Zep project for agricultural education

const zepConfig = {
  api_url: $env.ZEP_API_URL,
  api_key: $env.ZEP_API_KEY,
  project_uuid: $env.ZEP_PROJECT_UUID
};

// Memory configuration settings
const memorySettings = {
  memory_window_days: 90,
  session_timeout_hours: 24,
  auto_summarize: true,
  enable_entity_extraction: true,
  enable_relationship_extraction: true,

  // Agricultural education specific settings
  custom_entities: [
    {
      name: 'agricultural_concept',
      description: 'Agricultural education concepts and skills',
      examples: ['animal health', 'nutrition management', 'breeding protocols']
    },
    {
      name: 'ffa_competency',
      description: 'FFA competency standards',
      examples: ['AS.07.01', 'AS.07.02', 'AS.07.03', 'AS.07.04']
    },
    {
      name: 'animal_species',
      description: 'Livestock species',
      examples: ['cattle', 'sheep', 'swine', 'goats', 'poultry']
    },
    {
      name: 'learning_activity',
      description: 'Educational activities',
      examples: ['health check', 'journal entry', 'competency assessment']
    }
  ],

  relationship_types: [
    'demonstrates_competency',
    'involves_animal',
    'builds_upon',
    'requires_prerequisite',
    'leads_to_skill'
  ]
};

// Session ID pattern for agricultural education
const sessionIdPattern = 'student_{student_id}_{date}';

return {
  message: 'Zep memory configuration prepared for agricultural education',
  settings: memorySettings,
  session_pattern: sessionIdPattern,
  configured_for: 'ShowTrackAI Agricultural Education Platform'
};
